mapper.select:
SELECT 

FROM ASSESS_APPLY a

WHERE a.assess_apply_id = #{id}

SELECT
FROM ASSESS_APPLY a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.patient_applyer LIKE '%'||#{patientApplyer}||'%' '%'+#{patientApplyer}+'%' concat('%',#{patientApplyer},'%')
AND a.patient_mobile LIKE '%'||#{patientMobile}||'%' '%'+#{patientMobile}+'%' concat('%',#{patientMobile},'%')
AND a.patient_name = #{patientName}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM ASSESS_APPLY a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ASSESS_APPLY SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE assess_apply_id = #{id}
