mapper.select:
SELECT 

FROM COMPLAINT_INFO  a

WHERE a.complaint_id = #{complaintId}

SELECT
FROM COMPLAINT_MAIN b LEFT JOIN COMPLAINT_INFO a ON a.complaint_main_id = b.complaint_main_id LEFT JOIN SYS_OFFICE o1 ON o1.id = a.involve_hospital LEFT JOIN SYS_OFFICE o2 ON o2.id = a.involve_department LEFT JOIN SYS_DICT o3 ON o3.`value` = a.patient_relation AND o3.type = 'patient_relation' LEFT JOIN SYS_DICT o4 ON o4.value = a.patient_sex AND o4.type = 'sex' LEFT JOIN SYS_USER o5 ON o5.id = a.involve_employee LEFT JOIN SYS_USER u1 ON u1.id = a.reception_employee LEFT JOIN SYS_USER u2 ON u2.id = a.next_link_man WHERE b.case_number = #{caseNumber}
AND b.complaint_main_id != ${complaintMainId}
