mapper.select:
SELECT 

FROM COMPLAINT_MAIN_DETAIL a

WHERE a.complaint_main_detail_id = #{id}

SELECT
FROM COMPLAINT_MAIN_DETAIL a
/*在投诉接待列表 主表的实例是空的 如果有实例ID 不会在当前节点*/ a.del_flag = #{DEL_FLAG_NORMAL} and b.PROC_INS_ID is NULL
AND a.visitor_name LIKE '%'||#{visitorName}||'%' '%'+#{visitorName}+'%' concat('%',#{visitorName},'%')
AND a.visitor_mobile LIKE '%'||#{visitorMobile}||'%' '%'+#{visitorMobile}+'%' concat('%',#{visitorMobile},'%')
AND a.visitor_date BETWEEN #{beginVisitorDate} AND #{endVisitorDate}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM COMPLAINT_MAIN_DETAIL a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE COMPLAINT_MAIN_DETAIL SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE complaint_main_detail_id = #{id}
