mapper.select:
SELECT

FROM machine_account a

WHERE a.machine_account_id = #{id}

SELECT
FROM machine_account a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.reporting_time LIKE '%'||#{reportingTime}||'%' '%'+#{reportingTime}+'%' concat('%',#{reportingTime},'%')
AND a.dept_id LIKE '%'||#{deptId}||'%' '%'+#{deptId}+'%' concat('%',#{deptId},'%')
AND a.mediator_id LIKE '%'||#{mediatorId}||'%' '%'+#{mediatorId}+'%' concat('%',#{mediatorId},'%')
AND a.hospital_id LIKE '%'||#{hospitalId}||'%' '%'+#{hospitalId}+'%' concat('%',#{hospitalId},'%')
AND a.policy_number = #{policyNumber}
AND a.start_insurance_time = #{startInsuranceTime}
AND a.file_number LIKE '%'||#{fileNumber}||'%' '%'+#{fileNumber}+'%' concat('%',#{fileNumber},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT

FROM machine_account a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE machine_account SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE machine_account_id = #{id}

mapper.select:
SELECT

FROM machine_account a

WHERE a.file_number = #{fileNumber}
